mapper.select:
select
    *
from appointments
where house_id = #{id}
  and status in ('PENDING', 'CONFIRMED')
  and #{endOfDay} >= start_time  and end_time >= #{startOfDay}

select ap.*, h.title, h.address, u.username from appointments ap left join houses h on ap.house_id = h.house_id left join users u on ap.user_id = u.user_id
and h.title like concat('%', #{query.title}, '%')
and ap.status = #{query.status}
and ap.start_time >= #{query.startTime}
and ap.end_time <= #{query.endTime}
order by ap.create_time desc
